mapper.insert:
INSERT INTO MEMBER VALUES(
	SEQ_MID.NEXTVAL, DEFAULT, #{userId}, #{userPwd}, #{userName}, #{userPhone}, DEFAULT, DEFAULT, null
)

mapper.select:
SELECT COUNT(*)
FROM MEMBER
WHERE USERID=#{userId}

SELECT COUNT(*)
FROM MEMBER
WHERE KAKAO=#{kakaoId}

mapper.insert:
INSERT INTO MEMBER VALUES(
	SEQ_MID.NEXTVAL, DEFAULT, #{userId}, null, #{userName}, #{userPhone}, DEFAULT, DEFAULT, #{kakao}
)

mapper.update:
UPDATE MEMBER SET
USERPWD=#{userPwd}
WHERE USERID=#{userId}

mapper.select:
SELECT COUNT(*)
FROM MEMBER
WHERE USERID=#{userId}
AND USERNAME=#{userName}

SELECT COUNT(*)
FROM MEMBER
WHERE USERID=#{userId}
AND USERPHONE=#{userPhone}
