mapper.insert:
		INSERT INTO NOTICE VALUES(
         SEQ_NID.NEXTVAL, #{user_T}, #{nTitle}, #{nContent}, SYSDATE, SYSDATE, #{nStatus}
      )

mapper.update:
UPDATE NOTICE
SET USER_T=#{user_T}, NTITLE=#{nTitle}, NCONTENT=#{nContent},
	N_MODIFY_DATE=SYSDATE,
	N_STATUS=#{nStatus}
WHERE NID=#{nId}
